changeSet.sql:
Create table if not exist dreams ( id uuid PRIMARY KEY, dayStart timestamp, dayFinish timestamp, nightStart timestamp, nightFinish timestamp, FOREIGN KEY (tag) REFERENCES tag (tag), FOREIGN KEY (comment) REFERENCES comment (comment) ); Create table if not exist users ( id uuid PRIMARY KEY, mail varchar NOT NULL, password varchar NOT NULL, childName varchar NOT NULL, dateOfBirth timestamp NOT NULL, temperament varchar, momName varchar, dadName varchar, childPhotoId varchar, momPhotoId varchar, dadPhotoId varchar );
Create table if not exist comment ( id uuid PRIMARY KEY, comment varchar );
Create table if not exist tag ( id uuid PRIMARY KEY, tag varchar );
